import React, { useRef, useState, useEffect, useCallback } from 'react';
import ImageWithFallback from './ImageWithFallback';
import './InfiniteMovieRow.css';

const InfiniteMovieRow = ({ title, genreLink, movies, fetchMoreMovies }) => {
  const sliderRef = useRef(null);
  const observerRef = useRef(null);
  const [currentPage, setCurrentPage] = useState(0);
  const [hoveredMovie, setHoveredMovie] = useState(null);
  const [hoverTimeout, setHoverTimeout] = useState(null);
  const [canScrollLeft, setCanScrollLeft] = useState(false);
  const [canScrollRight, setCanScrollRight] = useState(true);
  const [isLoading, setIsLoading] = useState(false);
  const [visibleItems, setVisibleItems] = useState(new Set());
  const [allMovies, setAllMovies] = useState(movies);

  const itemsPerPage = 6;
  const totalPages = Math.ceil(allMovies.length / itemsPerPage);

  // Intersection Observer for lazy loading and animations
  useEffect(() => {
    const observer = new IntersectionObserver(
      (entries) => {
        entries.forEach((entry) => {
          if (entry.isIntersecting) {
            const index = parseInt(entry.target.dataset.index);
            setVisibleItems(prev => new Set([...prev, index]));
          }
        });
      },
      {
        threshold: 0.1,
        rootMargin: '50px'
      }
    );

    observerRef.current = observer;

    return () => {
      if (observerRef.current) {
        observerRef.current.disconnect();
      }
    };
  }, []);

  // Observe movie items when they're rendered
  useEffect(() => {
    if (sliderRef.current && observerRef.current) {
      const items = sliderRef.current.querySelectorAll('.infinite-slider-item');
      items.forEach((item) => {
        observerRef.current.observe(item);
      });
    }
  }, [allMovies]);

  // Load more movies when reaching the end
  const loadMoreMovies = useCallback(async () => {
    if (isLoading || !fetchMoreMovies) return;

    setIsLoading(true);
    try {
      const newMovies = await fetchMoreMovies();
      setAllMovies(prev => [...prev, ...newMovies]);
    } catch (error) {
      console.error('Failed to load more movies:', error);
    } finally {
      setIsLoading(false);
    }
  }, [isLoading, fetchMoreMovies]);

  // Check if we need to load more movies
  useEffect(() => {
    const handleScroll = () => {
      if (!sliderRef.current) return;

      const { scrollLeft, scrollWidth, clientWidth } = sliderRef.current;
      const scrollPercentage = (scrollLeft + clientWidth) / scrollWidth;

      // Load more when 80% scrolled
      if (scrollPercentage > 0.8 && !isLoading) {
        loadMoreMovies();
      }

      updateScrollButtons();
      updateCurrentPage();
    };

    const slider = sliderRef.current;
    if (slider) {
      slider.addEventListener('scroll', handleScroll);
      return () => slider.removeEventListener('scroll', handleScroll);
    }
  }, [loadMoreMovies, isLoading]);

  const updateScrollButtons = () => {
    if (!sliderRef.current) return;
    const { scrollLeft, scrollWidth, clientWidth } = sliderRef.current;
    setCanScrollLeft(scrollLeft > 0);
    setCanScrollRight(scrollLeft < scrollWidth - clientWidth - 10);
  };

  const updateCurrentPage = () => {
    if (!sliderRef.current) return;
    const item = sliderRef.current.querySelector('.infinite-slider-item');
    if (!item) return;
    const scrollLeft = sliderRef.current.scrollLeft;
    const page = Math.floor(scrollLeft / (item.clientWidth * itemsPerPage));
    setCurrentPage(Math.min(page, totalPages - 1));
  };

  const scrollSlider = (direction) => {
    if (!sliderRef.current) return;
    const item = sliderRef.current.querySelector('.infinite-slider-item');
    if (!item) return;

    const scrollAmount = item.clientWidth * itemsPerPage;
    const currentScroll = sliderRef.current.scrollLeft;

    let targetScroll;
    if (direction === 'left') {
      targetScroll = Math.max(currentScroll - scrollAmount, 0);
    } else {
      targetScroll = currentScroll + scrollAmount;
    }

    // Smooth scroll with custom easing
    const startTime = performance.now();
    const duration = 500;
    const startScroll = currentScroll;
    const distance = targetScroll - startScroll;

    const easeInOutCubic = (t) => {
      return t < 0.5 ? 4 * t * t * t : (t - 1) * (2 * t - 2) * (2 * t - 2) + 1;
    };

    const animateScroll = (currentTime) => {
      const elapsed = currentTime - startTime;
      const progress = Math.min(elapsed / duration, 1);
      const easedProgress = easeInOutCubic(progress);

      sliderRef.current.scrollLeft = startScroll + (distance * easedProgress);

      if (progress < 1) {
        requestAnimationFrame(animateScroll);
      }
    };

    requestAnimationFrame(animateScroll);
  };

  const handleMouseEnter = (movie, index) => {
    if (hoverTimeout) {
      clearTimeout(hoverTimeout);
    }
    const timeout = setTimeout(() => {
      setHoveredMovie({ ...movie, index });
    }, 300);
    setHoverTimeout(timeout);
  };

  const handleMouseLeave = () => {
    if (hoverTimeout) {
      clearTimeout(hoverTimeout);
    }
    const timeout = setTimeout(() => {
      setHoveredMovie(null);
    }, 200);
    setHoverTimeout(timeout);
  };

  const handlePlayClick = (movie) => {
    console.log('Playing:', movie.title);
  };

  const handleAddToList = (movie) => {
    console.log('Added to list:', movie.title);
  };

  return (
    <div className="infinite-lolomoRow" data-list-context="genre">
      <h2 className="infinite-rowHeader">
        <a href={genreLink} className="infinite-rowTitle">
          <div className="infinite-row-header-title">{title}</div>
          <div className="infinite-aro-row-header">
            <div className="infinite-see-all-link">Explore All</div>
            <div className="infinite-aro-row-chevron">â€º</div>
          </div>
        </a>
        <ul className="infinite-pagination-indicator">
          {Array.from({ length: Math.min(totalPages, 10) }).map((_, idx) => (
            <li key={idx} className={idx === currentPage ? "active" : ""}></li>
          ))}
        </ul>
      </h2>

      <div className="infinite-rowContainer">
        <div className="infinite-rowContent">
          <div className="infinite-slider">
            {/* Left Arrow */}
            {canScrollLeft && (
              <span
                className="infinite-handle infinite-handlePrev"
                onClick={() => scrollSlider('left')}
              >
                <svg className="infinite-indicator-icon" viewBox="0 0 24 24">
                  <path d="M15 18l-6-6 6-6"/>
                </svg>
              </span>
            )}

            {/* Carousel */}
            <div className="infinite-sliderMask">
              <div
                className="infinite-sliderContent"
                ref={sliderRef}
              >
                {allMovies.map((movie, index) => (
                  <div 
                    key={`${movie.title}-${index}`}
                    data-index={index}
                    className={`infinite-slider-item ${
                      visibleItems.has(index) ? 'visible' : ''
                    } ${hoveredMovie?.index === index ? 'hovered' : ''}`}
                    onMouseEnter={() => handleMouseEnter(movie, index)}
                    onMouseLeave={handleMouseLeave}
                  >
                    <div className="infinite-title-card-container">
                      <div className="infinite-title-card">
                        <a
                          href={movie.link}
                          className="infinite-slider-refocus"
                        >
                          <div className="infinite-boxart-container">
                            <ImageWithFallback
                              className="infinite-boxart-image"
                              src={movie.image}
                              alt={movie.title}
                              fallbackText={movie.title}
                              loading="lazy"
                            />
                            <div className="infinite-image-overlay"></div>
                          </div>
                        </a>

                        {/* Enhanced Hover Card */}
                        {hoveredMovie?.index === index && (
                          <div className="infinite-hover-card">
                            <div className="infinite-hover-image">
                              <ImageWithFallback 
                                src={movie.image} 
                                alt={movie.title}
                                fallbackText={movie.title}
                              />
                              <div className="infinite-hover-play-btn">
                                <svg viewBox="0 0 24 24">
                                  <path d="M8 5v14l11-7z"/>
                                </svg>
                              </div>
                            </div>

                            <div className="infinite-hover-content">
                              <div className="infinite-hover-actions">
                                <button 
                                  className="infinite-action-btn infinite-play-btn"
                                  onClick={() => handlePlayClick(movie)}
                                >
                                  <svg viewBox="0 0 24 24">
                                    <path d="M8 5v14l11-7z"/>
                                  </svg>
                                </button>

                                <button 
                                  className="infinite-action-btn infinite-add-btn"
                                  onClick={() => handleAddToList(movie)}
                                >
                                  <svg viewBox="0 0 24 24">
                                    <path d="M12 5v14M5 12h14"/>
                                  </svg>
                                </button>

                                <button className="infinite-action-btn infinite-like-btn">
                                  <svg viewBox="0 0 24 24">
                                    <path d="M14 9V5a3 3 0 0 0-3-3l-4 9v11h11.28a2 2 0 0 0 2-1.7l1.38-9a2 2 0 0 0-2-2.3zM7 22H4a2 2 0 0 1-2-2v-7a2 2 0 0 1 2-2h3"/>
                                  </svg>
                                </button>

                                <button className="infinite-action-btn infinite-dropdown-btn">
                                  <svg viewBox="0 0 24 24">
                                    <path d="M6 9l6 6 6-6"/>
                                  </svg>
                                </button>
                              </div>

                              <div className="infinite-hover-info">
                                <div className="infinite-hover-match">
                                  <span className="infinite-match-score">{movie.match || '98'}% Match</span>
                                </div>

                                <div className="infinite-hover-metadata">
                                  <span className="infinite-hover-rating">{movie.rating || 'TV-MA'}</span>
                                  <span className="infinite-hover-year">{movie.year || '2023'}</span>
                                  <span className="infinite-hover-quality">HD</span>
                                </div>

                                <div className="infinite-hover-genres">
                                  {(movie.genres || ['Action', 'Drama', 'Thriller']).map((genre, idx) => (
                                    <span key={idx} className="infinite-genre-tag">
                                      {genre}
                                    </span>
                                  ))}
                                </div>
                              </div>
                            </div>
                          </div>
                        )}

                        {/* Simple Title Overlay for Non-Hovered Items */}
                        {hoveredMovie?.index !== index && (
                          <div className="infinite-simple-overlay">
                            <p className="infinite-simple-title">{movie.title}</p>
                          </div>
                        )}
                      </div>
                    </div>
                  </div>
                ))}

                {/* Loading indicator */}
                {isLoading && (
                  <div className="infinite-loading-container">
                    <div className="infinite-loading-spinner"></div>
                  </div>
                )}
              </div>
            </div>

            {/* Right Arrow */}
            {canScrollRight && (
              <span
                className="infinite-handle infinite-handleNext"
                onClick={() => scrollSlider('right')}
              >
                <svg className="infinite-indicator-icon" viewBox="0 0 24 24">
                  <path d="M9 18l6-6-6-6"/>
                </svg>
              </span>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default InfiniteMovieRow;
