import React, { useState, useRef } from 'react';
import { Link } from 'react-router-dom';
import './Header.css';

const Navbar = ({ onSectionClick }) => {
  const [showProfileMenu, setShowProfileMenu] = useState(false);
  const [showNotificationMenu, setShowNotificationMenu] = useState(false);
  const notificationTimeoutRef = useRef(null);
  const profileTimeoutRef = useRef(null);

  // Notification Dropdown Handlers
  const handleNotificationEnter = () => {
    if (notificationTimeoutRef.current) {
      clearTimeout(notificationTimeoutRef.current);
      notificationTimeoutRef.current = null;
    }
    setShowProfileMenu(false);
    setShowNotificationMenu(true);
  };

  const handleNotificationLeave = () => {
    notificationTimeoutRef.current = setTimeout(() => {
      setShowNotificationMenu(false);
    }, 180); // Slightly reduced timeout
  };

  // Profile Dropdown Handlers
  const handleProfileEnter = () => {
    if (profileTimeoutRef.current) {
      clearTimeout(profileTimeoutRef.current);
      profileTimeoutRef.current = null;
    }
    setShowNotificationMenu(false);
    setShowProfileMenu(true);
  };

  const handleProfileLeave = () => {
    profileTimeoutRef.current = setTimeout(() => {
      setShowProfileMenu(false);
    }, 180); // Slightly reduced timeout
  };

  const handleLogoClick = () => {
    window.location.href = '/';
  };

  const handleSignOut = (e) => {
    e.preventDefault();
    localStorage.removeItem('user');
    window.location.href = '/login';
  };

  return (
    <div className="netflix-header">
      <div className="navbar-left">
        <img
          src="https://upload.wikimedia.org/wikipedia/commons/0/08/Netflix_2015_logo.svg"
          alt="Netflix"
          className="navbar-logo"
          onClick={handleLogoClick}
          tabIndex={0}
          onKeyDown={(e) => e.key === "Enter" && handleLogoClick()}
        />
        <ul className="primary-navigation">
          <li><a href="#" className="nav-link" onClick={() => onSectionClick && onSectionClick("home")}>Home</a></li>
          <li><a href="#" className="nav-link" onClick={() => onSectionClick && onSectionClick("tvshows")}>TV Shows</a></li>
          <li><a href="#" className="nav-link active" onClick={() => onSectionClick && onSectionClick("movies")}>Movies</a></li>
          <li><a href="#" className="nav-link" onClick={() => onSectionClick && onSectionClick("newpopular")}>New & Popular</a></li>
          <li><a href="#" className="nav-link" onClick={() => onSectionClick && onSectionClick("mylist")}>My List</a></li>
          <li><a href="#" className="nav-link" onClick={() => onSectionClick && onSectionClick("languages")}>Browse by Languages</a></li>
        </ul>
      </div>
      here

      {/* Right side navigation */}
      <div className="secondary-navigation">
        <div className="nav-element">
          <button className="nav-button" aria-label="Search">
            <svg className="nav-icon" viewBox="0 0 24 24">
              <path fillRule="evenodd" clipRule="evenodd" d="M17 10C17 13.866 13.866 17 10 17C6.13401 17 3 13.866 3 10C3 6.13401 6.13401 3 10 3C13.866 3 17 6.13401 17 10ZM15.6177 17.0319C14.078 18.2635 12.125 19 10 19C5.02944 19 1 14.9706 1 10C1 5.02944 5.02944 1 10 1C14.9706 1 19 5.02944 19 10C19 12.125 18.2635 14.078 17.0319 15.6177L22.7071 21.2929L21.2929 22.7071L15.6177 17.0319Z" fill="currentColor"></path>
            </svg>
          </button>
        </div>

        <div className="nav-element">
          <Link to="/childrenhome" className="nav-link">Children</Link>
        </div>

        {/* Notification Dropdown */}
        <div
          className="nav-element notification-menu-item"
          onMouseEnter={handleNotificationEnter}
          onMouseLeave={handleNotificationLeave}
        >
          <button className="nav-button" aria-label="Notifications">
            <svg className="nav-icon" viewBox="0 0 24 24">
              <path fillRule="evenodd" clipRule="evenodd" d="M13.0002 4.07092C16.3924 4.55624 19 7.4736 19 11V15.2538C20.0489 15.3307 21.0851 15.4245 22.1072 15.5347L21.8928 17.5232C18.7222 17.1813 15.4092 17 12 17C8.59081 17 5.27788 17.1813 2.10723 17.5232L1.89282 15.5347C2.91498 15.4245 3.95119 15.3307 5.00003 15.2538V11C5.00003 7.47345 7.60784 4.55599 11.0002 4.07086V2H13.0002V4.07092ZM17 15.1287V11C17 8.23858 14.7614 6 12 6C9.2386 6 7.00003 8.23858 7.00003 11V15.1287C8.64066 15.0437 10.3091 15 12 15C13.691 15 15.3594 15.0437 17 15.1287ZM8.62593 19.3712C8.66235 20.5173 10.1512 22 11.9996 22C13.848 22 15.3368 20.5173 15.3732 19.3712C15.3803 19.1489 15.1758 19 14.9533 19H9.0458C8.82333 19 8.61886 19.1489 8.62593 19.3712Z" fill="currentColor"></path>
            </svg>
            <span className="notification-badge">3</span>
          </button>
          {showNotificationMenu && (
            <div className="notification-drop-down sub-menu theme-lakira" role="menu" tabIndex="0">
              <div className="ptrack-content">
                <ul className="sub-menu-list notifications" role="list" aria-label="Notifications">
                  <li className="sub-menu-item notification">
                    <div className="notification-item">
                      <span>New movie added: Extraction 2</span>
                    </div>
                  </li>
                  <li className="sub-menu-item notification">
                    <div className="notification-item">
                      <span>Trending: The Witcher Season 3</span>
                    </div>
                  </li>
                  <li className="sub-menu-item notification">
                    <div className="notification-item">
                      <span>Recommended: Heart of Stone</span>
                    </div>
                  </li>
                </ul>
              </div>
            </div>
          )}
        </div>

        {/* Profile Dropdown */}
        <div
          className="nav-element account-menu-item"
          onMouseEnter={handleProfileEnter}
          onMouseLeave={handleProfileLeave}
        >
          <div className="account-dropdown-button">
            <div className="avatar-wrapper">
              <img
                className="profile-icon"
                src="https://occ-0-2087-2186.1.nflxso.net/dnm/api/v6/vN7bi_My87NPKvsBoib006Llxzg/AAAABX4L9w79aT7nijZVgdNGkMraiqSPBtchAJv6ufbTi63POCqjyeH33eWZhDE7GITmH28pVz8rDrrSiJTYJIukt6h34Nyoqnw.png?r=eda"
                alt=""
              />
              <div className="callout-arrow"></div>
            </div>
            <div className={`caret ${showProfileMenu ? 'open' : ''}`}></div>
          </div>
          {showProfileMenu && (
            <div
              className="account-drop-down sub-menu theme-lakira"
              role="menu"
              tabIndex="0"
              data-uia="account-dropdown-menu"
            >
              <div className="ptrack-content">
                <ul className="sub-menu-list profiles" role="list" aria-label="Profiles">
                  <li className="sub-menu-item profile">
                    <div>
                      <a className="profile-link" href="/profile">
                        <div className="avatar-wrapper">
                          <img 
                            className="profile-icon" 
                            src="https://occ-0-7338-2186.1.nflxso.net/dnm/api/v6/vN7bi_My87NPKvsBoib006Llxzg/AAAABX4L9w79aT7nijZVgdNGkMraiqSPBtchAJv6ufbTi63POCqjyeH33eWZhDE7GITmH28pVz8rDrrSiJTYJIukt6h34Nyoqnw.png?r=eda" 
                            alt="" 
                          />
                        </div>
                        Bhushan {/* Profile name directly inside the <a> tag */}
                      </a>
                    </div>
                  </li>
                  <li className="sub-menu-item profile">
                    <div>
                      <a className="profile-link" href="/profile">
                        <div className="avatar-wrapper">
                          <img 
                            className="profile-icon" 
                            src="https://occ-0-7338-2186.1.nflxso.net/dnm/api/v6/vN7bi_My87NPKvsBoib006Llxzg/AAAABXz4LMjJFidX8MxhZ6qro8PBTjmHbxlaLAbk45W1DXbKsAIOwyHQPiMAuUnF1G24CLi7InJHK4Ge4jkXul1xIW49Dr5S7fc.png?r=e6e" 
                            alt="Profile 1" 
                          />
                        </div>
                        Nikita {/* Profile name directly inside the <a> tag */}
                      </a>
                    </div>
                  </li>
                  <li className="sub-menu-item profile">
                    <div>
                      <a className="profile-link" href="/profile">
                        <div className="avatar-wrapper">
                          <img 
                            className="profile-icon" 
                            src="https://occ-0-7338-2186.1.nflxso.net/dnm/api/v6/vN7bi_My87NPKvsBoib006Llxzg/AAAABeuqjuQsRgqEDlibtJTI5BMf8IxhLlLOeIT6xI4TL57mqv7XHja43gx02S8pZVe8JNGRQXjnrUk1VcsTXqi83tFKPI6OR3k.png?r=bd7" 
                            alt="Profile 2" 
                          />
                        </div>
                        Bhakti {/* Profile name directly inside the <a> tag */}
                      </a>
                    </div>
                  </li>
                </ul>

                <ul className="account-links sub-menu-list" aria-label="Account">
                  <li className="sub-menu-item profile-link" role="listitem">
                    <a className="sub-menu-link sub-menu-link-icon" href="/account/profiles">
                      <svg className="svg-icon" viewBox="0 0 24 24">
                        <path fill="currentColor" d="M19.1213 1.7071C17.9497 0.535532 16.0503 0.53553 14.8787 1.7071L13.2929 3.29289L12.5858 4L1.58579 15C1.21071 15.3751 1 15.8838 1 16.4142V21C1 22.1046 1.89543 23 3 23H7.58579C8.11622 23 8.62493 22.7893 9 22.4142L20 11.4142L20.7071 10.7071L22.2929 9.12132C23.4645 7.94975 23.4645 6.05025 22.2929 4.87868L19.1213 1.7071Z"/>
                      </svg>
                      <span>Manage Profiles</span>
                    </a>
                  </li>
                  <li className="sub-menu-item">
                    <a className="sub-menu-link sub-menu-link-icon" href="/account/profile/transfer">
                      <svg className="svg-icon" viewBox="0 0 24 24">
                        <path fill="currentColor" d="M6 1C3.79086 1 2 2.79086 2 5V17C2 19.2091 3.79086 21 6 21H9.58579L8.29289 22.2929L9.70711 23.7071L12.7071 20.7071C13.0976 20.3166 13.0976 19.6834 12.7071 19.2929L9.70711 16.2929L8.29289 17.7071L9.58579 19H6C4.89543 19 4 18.1046 4 17V5C4 3.89543 4.89543 3 6 3H18C19.1046 3 20 3.89543 20 5V17C20 18.1046 19.1046 19 18 19H15V21H18C20.2091 21 22 19.2091 22 17V5C22 2.79086 20.2091 1 18 1H6ZM7.5 10C8.32843 10 9 9.32843 9 8.5C9 7.67157 8.32843 7 7.5 7C6.67157 7 6 7.67157 6 8.5C6 9.32843 6.67157 10 7.5 10ZM18 8.5C18 9.32843 17.3284 10 16.5 10C15.6716 10 15 9.32843 15 8.5C15 7.67157 15.6716 7 16.5 7C17.3284 7 18 7.67157 18 8.5ZM16.402 12.1985C15.7973 12.6498 14.7579 13 13.5 13C12.2421 13 11.2027 12.6498 10.598 12.1985L9.40195 13.8015C10.4298 14.5684 11.9192 15 13.5 15C15.0808 15 16.5702 14.5684 17.598 13.8015L16.402 12.1985Z"/>
                      </svg>
                      <span>Transfer Profile</span>
                    </a>
                  </li>
                  <li className="sub-menu-item">
                    <a className="sub-menu-link sub-menu-link-icon" href="/account">
                      <svg className="svg-icon" viewBox="0 0 24 24">
                        <path fill="currentColor" d="M15 5C15 6.65685 13.6569 8 12 8C10.3431 8 9 6.65685 9 5C9 3.34315 10.3431 2 12 2C13.6569 2 15 3.34315 15 5ZM17 5C17 7.76142 14.7614 10 12 10C9.23858 10 7 7.76142 7 5C7 2.23858 9.23858 0 12 0C14.7614 0 17 2.23858 17 5ZM4 21C4 16.5817 7.58172 13 12 13C16.4183 13 20 16.5817 20 21V21.5136C19.5678 21.5667 18.9844 21.6327 18.2814 21.6988C16.6787 21.8495 14.461 22 12 22C9.53901 22 7.32131 21.8495 5.71861 21.6988C5.01564 21.6327 4.43224 21.5667 4 21.5136V21ZM21.1508 23.3775C21.1509 23.3774 21.151 23.3774 21 22.3889L21.151 23.3774C21.6393 23.3028 22 22.8829 22 22.3889V21C22 15.4772 17.5228 11 12 11C6.47715 11 2 15.4772 2 21V22.3889C2 22.8829 2.36067 23.3028 2.84897 23.3774L3 22.3889C2.84897 23.3774 2.84908 23.3774 2.8492 23.3775L2.84952 23.3775L2.85043 23.3776L2.85334 23.3781L2.86352 23.3796L2.90103 23.3852C2.93357 23.3899 2.98105 23.3968 3.04275 23.4055C3.16613 23.4228 3.3464 23.4472 3.57769 23.4765C4.04018 23.535 4.7071 23.6126 5.5314 23.6901C7.1787 23.8449 9.461 24 12 24C14.539 24 16.8213 23.8449 18.4686 23.6901C19.2929 23.6126 19.9598 23.535 20.4223 23.4765C20.6536 23.4472 20.8339 23.4228 20.9573 23.4055C21.0189 23.3968 21.0664 23.3899 21.099 23.3852L21.1365 23.3796L21.1467 23.3781L21.1496 23.3776L21.1505 23.3775L21.1508 23.3775Z"/>
                      </svg>
                      <span>Account</span>
                    </a>
                  </li>
                  <li className="sub-menu-item">
