import React, { useState } from 'react';

const ImageWithFallback = ({ 
  src, 
  alt, 
  className, 
  fallbackSrc = null,
  fallbackText = null,
  ...props 
}) => {
  const [imageError, setImageError] = useState(false);
  const [loading, setLoading] = useState(true);

  const handleImageError = () => {
    setImageError(true);
    setLoading(false);
  };

  const handleImageLoad = () => {
    setLoading(false);
  };

  // Generate a fallback image URL based on the title
  const generateFallbackImage = (text) => {
    const colors = [
      '#e50914', '#8B0000', '#DC143C', '#4B0082', 
      '#2F4F4F', '#228B22', '#B8860B', '#FF1493',
      '#191970', '#FF4500', '#32CD32', '#FFD700'
    ];
    const randomColor = colors[Math.floor(Math.random() * colors.length)];
    const encodedText = encodeURIComponent(text || alt || 'Movie');
    return `https://via.placeholder.com/300x450/${randomColor.slice(1)}/ffffff?text=${encodedText}`;
  };

  const finalFallbackSrc = fallbackSrc || generateFallbackImage(fallbackText);

  if (imageError) {
    return (
      <img
        src={finalFallbackSrc}
        alt={alt}
        className={className}
        {...props}
      />
    );
  }

  return (
    <>
      {loading && (
        <div 
          className={`${className} loading-placeholder`}
          style={{
            background: 'linear-gradient(90deg, #333 0%, #444 50%, #333 100%)',
            backgroundSize: '200% 100%',
            animation: 'loadingShimmer 1.5s infinite'
          }}
        />
      )}
      <img
        src={src}
        alt={alt}
        className={`${className} ${loading ? 'loading' : ''}`}
        onError={handleImageError}
        onLoad={handleImageLoad}
        style={{ display: loading ? 'none' : 'block' }}
        {...props}
      />
    </>
  );
};

export default ImageWithFallback;
