import React, { useRef, useState, useEffect } from 'react';
import ImageWithFallback from './ImageWithFallback';
import './EnhancedMovieRow.css';

const EnhancedMovieRow = ({ title, genreLink, movies }) => {
  const sliderRef = useRef(null);
  const [currentPage, setCurrentPage] = useState(0);
  const [hoveredMovie, setHoveredMovie] = useState(null);
  const [hoverTimeout, setHoverTimeout] = useState(null);
  const [canScrollLeft, setCanScrollLeft] = useState(false);
  const [canScrollRight, setCanScrollRight] = useState(true);

  const itemsPerPage = 6;
  const totalPages = Math.ceil(movies.length / itemsPerPage);

  useEffect(() => {
    updateScrollButtons();
  }, []);

  const updateScrollButtons = () => {
    if (!sliderRef.current) return;
    const { scrollLeft, scrollWidth, clientWidth } = sliderRef.current;
    setCanScrollLeft(scrollLeft > 0);
    setCanScrollRight(scrollLeft < scrollWidth - clientWidth - 10);
  };

  const scrollSlider = (direction) => {
    if (!sliderRef.current) return;
    const item = sliderRef.current.querySelector('.enhanced-slider-item');
    if (!item) return;
    const scrollAmount = item.clientWidth * itemsPerPage;

    const lastSet = Math.max(movies.length - itemsPerPage, 0);
    const maxScroll = item.clientWidth * lastSet;

    let targetScroll;
    if (direction === 'left') {
      targetScroll = Math.max(
        sliderRef.current.scrollLeft - scrollAmount,
        0
      );
    } else {
      targetScroll = Math.min(
        sliderRef.current.scrollLeft + scrollAmount,
        maxScroll
      );
    }

    sliderRef.current.scrollTo({
      left: targetScroll,
      behavior: 'smooth'
    });

    setTimeout(updateScrollButtons, 300);
  };

  const handleScroll = () => {
    if (!sliderRef.current) return;
    const item = sliderRef.current.querySelector('.enhanced-slider-item');
    if (!item) return;
    const scrollLeft = sliderRef.current.scrollLeft;
    const page = Math.floor(scrollLeft / (item.clientWidth * itemsPerPage));
    setCurrentPage(page >= totalPages - 1 ? totalPages - 1 : page);
    updateScrollButtons();
  };

  const handleMouseEnter = (movie, index) => {
    if (hoverTimeout) {
      clearTimeout(hoverTimeout);
    }
    const timeout = setTimeout(() => {
      setHoveredMovie({ ...movie, index });
    }, 500);
    setHoverTimeout(timeout);
  };

  const handleMouseLeave = () => {
    if (hoverTimeout) {
      clearTimeout(hoverTimeout);
    }
    const timeout = setTimeout(() => {
      setHoveredMovie(null);
    }, 300);
    setHoverTimeout(timeout);
  };

  const handlePlayClick = (movie) => {
    console.log('Playing:', movie.title);
  };

  const handleAddToList = (movie) => {
    console.log('Added to list:', movie.title);
  };

  const handleLike = (movie) => {
    console.log('Liked:', movie.title);
  };

  const handleDislike = (movie) => {
    console.log('Disliked:', movie.title);
  };

  return (
    <div className="enhanced-lolomoRow" data-list-context="genre">
      <h2 className="enhanced-rowHeader">
        <a href={genreLink} className="enhanced-rowTitle">
          <div className="enhanced-row-header-title">{title}</div>
          <div className="enhanced-aro-row-header">
            <div className="enhanced-see-all-link">Explore All</div>
            <div className="enhanced-aro-row-chevron">›</div>
          </div>
        </a>
        <ul className="enhanced-pagination-indicator">
          {Array.from({ length: totalPages }).map((_, idx) => (
            <li key={idx} className={idx === currentPage ? "active" : ""}></li>
          ))}
        </ul>
      </h2>

      <div className="enhanced-rowContainer">
        <div className="enhanced-rowContent">
          <div className="enhanced-slider">
            {/* Left Arrow */}
            {canScrollLeft && (
              <span
                className="enhanced-handle enhanced-handlePrev"
                onClick={() => scrollSlider('left')}
              >
                <svg className="enhanced-indicator-icon" viewBox="0 0 24 24">
                  <path d="M15 18l-6-6 6-6"/>
                </svg>
              </span>
            )}

            {/* Carousel */}
            <div className="enhanced-sliderMask">
              <div
                className="enhanced-sliderContent"
                ref={sliderRef}
                onScroll={handleScroll}
              >
                {movies.map((movie, index) => (
                  <div 
                    key={index} 
                    className={`enhanced-slider-item ${hoveredMovie?.index === index ? 'hovered' : ''}`}
                    onMouseEnter={() => handleMouseEnter(movie, index)}
                    onMouseLeave={handleMouseLeave}
                  >
                    <div className="enhanced-title-card-container">
                      <div className="enhanced-title-card">
                        <a
                          href={movie.link}
                          className="enhanced-slider-refocus"
                        >
                          <div className="enhanced-boxart-container">
                            <ImageWithFallback
                              className="enhanced-boxart-image"
                              src={movie.image}
                              alt={movie.title}
                              fallbackText={movie.title}
                              loading="lazy"
                            />
                            <div className="enhanced-image-overlay"></div>
                          </div>
                        </a>

                        {/* Enhanced Hover Card */}
                        {hoveredMovie?.index === index && (
                          <div className="enhanced-hover-card">
                            <div className="enhanced-hover-image">
                              <ImageWithFallback 
                                src={movie.image} 
                                alt={movie.title}
                                fallbackText={movie.title}
                              />
                              <div className="enhanced-hover-play-btn">
                                <svg viewBox="0 0 24 24">
                                  <path d="M8 5v14l11-7z"/>
                                </svg>
                              </div>
                            </div>

                            <div className="enhanced-hover-content">
                              <div className="enhanced-hover-actions">
                                <button 
                                  className="enhanced-action-btn enhanced-play-btn"
                                  onClick={() => handlePlayClick(movie)}
                                >
                                  <svg viewBox="0 0 24 24">
                                    <path d="M8 5v14l11-7z"/>
                                  </svg>
                                </button>

                                <button 
                                  className="enhanced-action-btn enhanced-add-btn"
                                  onClick={() => handleAddToList(movie)}
                                >
                                  <svg viewBox="0 0 24 24">
                                    <path d="M12 5v14M5 12h14"/>
                                  </svg>
                                </button>

                                <button 
                                  className="enhanced-action-btn enhanced-like-btn"
                                  onClick={() => handleLike(movie)}
                                >
                                  <svg viewBox="0 0 24 24">
                                    <path d="M14 9V5a3 3 0 0 0-3-3l-4 9v11h11.28a2 2 0 0 0 2-1.7l1.38-9a2 2 0 0 0-2-2.3zM7 22H4a2 2 0 0 1-2-2v-7a2 2 0 0 1 2-2h3"/>
                                  </svg>
                                </button>

                                <button 
                                  className="enhanced-action-btn enhanced-dislike-btn"
                                  onClick={() => handleDislike(movie)}
                                >
                                  <svg viewBox="0 0 24 24">
                                    <path d="M10 15v4a3 3 0 0 0 3 3l4-9V2H5.72a2 2 0 0 0-2 1.7l-1.38 9a2 2 0 0 0 2 2.3zm7-13h2.67A2.31 2.31 0 0 1 22 4v7a2.31 2.31 0 0 1-2.33 2H17"/>
                                  </svg>
                                </button>

                                <button className="enhanced-action-btn enhanced-dropdown-btn">
                                  <svg viewBox="0 0 24 24">
                                    <path d="M6 9l6 6 6-6"/>
                                  </svg>
                                </button>
                              </div>

                              <div className="enhanced-hover-info">
                                <div className="enhanced-hover-match">
                                  <span className="enhanced-match-score">{movie.match || '98'}% Match</span>
                                </div>

                                <div className="enhanced-hover-metadata">
                                  <span className="enhanced-hover-rating">{movie.rating || 'TV-MA'}</span>
                                  <span className="enhanced-hover-year">{movie.year || '2023'}</span>
                                  <span className="enhanced-hover-quality">HD</span>
                                </div>

                                <div className="enhanced-hover-genres">
                                  {(movie.genres || ['Action', 'Drama', 'Thriller']).map((genre, idx) => (
                                    <span key={idx} className="enhanced-genre-tag">
                                      {genre}
                                    </span>
                                  ))}
                                </div>
                              </div>
                            </div>
                          </div>
                        )}

                        {/* Simple Title Overlay for Non-Hovered Items */}
                        {hoveredMovie?.index !== index && (
                          <div className="enhanced-simple-overlay">
                            <p className="enhanced-simple-title">{movie.title}</p>
                          </div>
                        )}
                      </div>
                    </div>
                  </div>
                ))}
              </div>
            </div>

            {/* Right Arrow */}
            {canScrollRight && (
              <span
                className="enhanced-handle enhanced-handleNext"
                onClick={() => scrollSlider('right')}
              >
                <svg className="enhanced-indicator-icon" viewBox="0 0 24 24">
                  <path d="M9 18l6-6-6-6"/>
                </svg>
              </span>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

